(async () => {
  try {
    // Intro
    console.log("[GUIDE SUMMARY] Setting up SSH keys is crucial for secure, passwordless authentication to remote servers. This guide will walk you through generating, configuring, and using SSH keys on macOS, explaining the importance of each step.");
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Step 1: Open Terminal
    console.log("[GUIDE STEP 1] We start by opening the Terminal application, which is essential for executing commands to manage SSH keys. Terminal provides direct access to the operating system's command line interface.");
    await findAndClick('terminal_icon.png'); await new Promise(resolve => setTimeout(resolve, 2000));
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Step 2: Generate SSH Key
    console.log("[GUIDE STEP 2] Generating an SSH key pair is the first step in setting up secure authentication. The key pair consists of a private key (kept secret) and a public key (shared with servers). This command creates a new key pair using RSA encryption.");
    await keyboard.type('ssh-keygen -t rsa -b 4096 -C "your_email@example.com"'); await keyboard.pressKey(Key.Return); await keyboard.releaseKey(Key.Return); await new Promise(resolve => setTimeout(resolve, 5000));
    await new Promise(resolve => setTimeout(resolve, 2000));

    // Step 3: Save SSH Key
    console.log("[GUIDE STEP 3] You'll be prompted to save the key. Pressing Enter without typing anything will save it to the default location (~/.ssh/id_rsa). This step is crucial as it determines where your private key will be stored.");
    await keyboard.pressKey(Key.Return); await keyboard.releaseKey(Key.Return); await new Promise(resolve => setTimeout(resolve, 2000));
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Step 4: Set Passphrase
    console.log("[GUIDE STEP 4] Setting a passphrase adds an extra layer of security to your private key. It's optional but recommended for sensitive operations. If you choose not to set one, just press Enter twice.");
    await keyboard.pressKey(Key.Return); await keyboard.releaseKey(Key.Return); await keyboard.pressKey(Key.Return); await keyboard.releaseKey(Key.Return); await new Promise(resolve => setTimeout(resolve, 3000));
    await new Promise(resolve => setTimeout(resolve, 1000));

    // Step 5: Copy Public Key
    console.log("[GUIDE STEP 5] The public key needs to be copied to the clipboard for easy transfer to remote servers. This step uses the `pbcopy` command, which is specific to macOS, to copy the contents of the public key file.");
    await keyboard.type('cat ~/.ssh/id_rsa.pub | pbcopy'); await keyboard.pressKey(Key.Return); await keyboard.releaseKey(Key.Return); await new Promise(resolve => setTimeout(resolve, 2000));
    await new Promise(resolve => setTimeout(resolve, 1000));

    console.log("[GUIDE COMPLETE] Guide finished successfully!");
  } catch (error) {
    console.error('[GUIDE ERROR]', error.message);
    throw error;
  }
})();

